mapper.select:
SELECT RM,NO,WRITER,TITLE,HIT,GRADE,REGDATE,FILENAME FROM( SELECT ROWNUM RM,NO,WRITER,TITLE,HIT,GRADE,REGDATE,FILENAME FROM( SELECT NO,WRITER,TITLE,HIT,CASE WHEN HIT BETWEEN 0 AND 10 THEN 1 WHEN HIT BETWEEN 11 AND 20 THEN 2 WHEN HIT BETWEEN 21 AND 50 THEN 3 WHEN HIT BETWEEN 51 AND 100 THEN 4 ELSE 5 END GRADE, TO_CHAR(REGDATE,'YYYY/MM/DD')REGDATE,FILENAME FROM BBS_SI
${query} LIKE '%'||#{data}||'%'
ORDER BY NO DESC)) WHERE RM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BBS_SI SET
HIT=HIT+1
WHERE NO=#{VALUE}

mapper.select:
SELECT PASSWORD
FROM SIMEMBER	
WHERE ID=#{VALUE}
